    <TodoList items={items} inputText={inputText} handleChange={handleChange} handleEnter={handleEnter} addItem={addItem} onDelete={onDelete} />
    <TodoList items={items} inputText={inputText} handleChange={handleChange} handleEnter={handleEnter} addItem={addItem} onDelete={onDelete} />
    <TodoList items={items} inputText={inputText} handleChange={handleChange} handleEnter={handleEnter} addItem={addItem} onDelete={onDelete} />

    </div>
    );
}

export default App;
