import React, { useState } from "react";
import TodoList from "./TodoList";
function App() {
  const [delItems, setDelItems] = useState([]);
  function updateDelItems(item) {
    setDelItems((prevDelItems) => {
      return [...prevDelItems, item];
    });
  }
  return (
    <div className="board">
      <TodoList name="To-Do" updateDelItems={updateDelItems} />
      {delItems.length ? (
        <TodoList name="Done" onlyDisplay="true" displayItems={delItems} />
      ) : (
        ""
      )}
      {console.log(delItems)}
      <div class="input-group input-group-sm mb-3 newList">
      <div class="input-group input-group-lg">
          <input
             type="text" class="form-control" aria-label="Large" aria-describedby="inputGroup-sizing-sm"
          />
        </div>
      </div>
    </div>
  );
}

export default App;
