import React, { useState } from "react";
import { Button, DropdownButton, Dropdown } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmarkSquare,faBars } from "@fortawesome/free-solid-svg-icons";

import Todo from "./Todo";
import "../css/todolist.css";

function TodoList(props) {
  const [inputText, setInputText] = useState("");
  const [items, setItems] = useState([]);

  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }
  function handleEnter(event) {
    if (event.key == "Enter") {
      event.preventDefault();
      setInputText(event.target.value);
      addItem();
    }
  }
  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, inputText];
    });
    setInputText("");
  }
  function onDelete(item) {
    setItems(
      items.filter((e) => {
        return e !== item.content;
      })
    );
    props.updateDelItems(item.content);
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>{props.heading}</h1>
        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
          <FontAwesomeIcon icon={faBars} />
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item
              as="button"
              className="delListButton"
              onClick={() => props.delList(props.heading)}
            >
              <span>
                <FontAwesomeIcon icon={faXmarkSquare} size="2x" />
              </span>
            </Dropdown.Item>
            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
        {/* <DropdownButton id="dropdown-item-button" title="">
          <Dropdown.Item
            as="button"
            className="delListButton"
            onClick={() => props.delList(props.heading)}
          >
            <span>
              <FontAwesomeIcon icon={faXmarkSquare} size="2x" />
            </span>
          </Dropdown.Item>
          <Dropdown.Item as="button">Another action</Dropdown.Item>
          <Dropdown.Item as="button">Something else</Dropdown.Item>
        </DropdownButton> */}
      </div>
      {!props.displayItems && (
        <div className="form">
          <input
            onChange={handleChange}
            onKeyPress={handleEnter}
            type="text"
            value={inputText}
          />
          <button className="addTodoButton" onClick={addItem}>
            <span>Add</span>
          </button>
        </div>
      )}
      <div>
        <ul>
          {props.onlyDisplay
            ? props.displayItems.map((todoItem) => (
                <Todo
                  key={todoItem}
                  content={todoItem}
                  onlyDisplay={props.onlyDisplay}
                />
              ))
            : items.map((todoItem) => (
                <Todo key={todoItem} content={todoItem} onDelete={onDelete} />
              ))}
        </ul>
      </div>
    </div>
  );
}
export default TodoList;
